numsamp=0

for signal in $all_signals; do
   numsamp=$((numsamp+1))	
done

echo "number of samples:" $numsamp >> notuseable.txt
echo "number of samples:" $numsamp >> useable.txt

echo "number of samples:" $numsamp
echo -n "number of samples checked: "

enum=1
for signal in $all_signals; do
   checkfile=$(edmDumpEventContent $signal | grep "displacedStandAlone")
#   echo $checkfile 
#   echo $signal
   echo -n $enum
   if [ -z "$checkfile" ]; then
      echo $signal >> notuseable.txt
   fi
   if [ ! -z "$checkfile" ]; then
       echo $signal >> useable.txt
   fi
   if [ $enum -lt 10 ]; then
      echo -n -e "\b"
   fi
   if [ $enum -ge 10 -a $enum -lt 100 ]; then
      echo -n -e "\b\b"
   fi
   if [ $enum -ge 100 -a $enum -lt 1000 ]; then
      echo -n -e "\b\b\b"
   fi
   enum=$((enum+1))
done
echo -e "\n"

